import { useState, useEffect } from "react";

const checkMark = new URL("../public/tick-red.png", import.meta.url);
const API_BASE = "http://localhost:3001";

function App() {

    const [todos, setTodos] = useState([]);
    const [popupActive, setPopupActive] = useState([]); //For adding new todo popup
    const [newTodo, setNewTodo] = useState(''); //New todo in add popup will be bound to this newTodo

    useEffect(() => { 
        GetTodos();

        console.log(todos)
    }, []) // [] is saying only call this when the module loads, only once. 

    const GetTodos = () => { 
        fetch(API_BASE + "/todos")
            .then(res => res.json())
            .then(data => setTodos(data))
            .catch(err => console.error("Error: ", err));
    }

	return (
		<div className="App">
            <h1>Welcome, User</h1>
            <h4>Your tasks</h4>

            <div className="todos">
                <div className="todo">
                    <div className="checkbox"></div>

                    <div className="text">Get the bread</div>

                    <div className="delete-todo">X</div>
                </div>

                <div className="todo is-complete">
                    <div className="checkbox">
                        <img src={ checkMark } alt="Checked"/>
                    </div>

                    <div className="text">Get the pizza</div>

                    <div className="delete-todo">X</div>
                </div>
            </div>
		</div>
	);
}

export default App;
