import { useState, useEffect } from "react";

const redTick = new URL("../public/tick-red.png", import.meta.url);
const API_BASE = "http://localhost:3001";

function App() {

    const [todos, setTodos] = useState([]);
    const [popupActive, setPopupActive] = useState([]); //For adding new todo popup
    const [newTodo, setNewTodo] = useState(''); //New todo in add popup will be bound to this newTodo

    useEffect(() => { 
        GetTodos();

        console.log(todos)
    }, []) // [] is saying only call this when the module loads, only once. 

    const GetTodos = () => { 
        fetch(API_BASE + "/todos")
            .then(res => res.json())
            .then(data => setTodos(data))
            .catch(err => console.error("Error: ", err));
    }

    const completeTodo = async id => { 
        const data = await fetch(API_BASE + "/todo/complete/" + id).then(res => res.json());
        console.log(data)
        setTodos(todos => todos.map(todo => { 
            if (todo._id === data._id) { 
                todo.complete = data.complete;
            }

            return todo;
        }))
    }

	return (
		<div className="App">
            <h1>Welcome, User</h1>
            <h4>Your tasks</h4>

            <div className="todos">
                {todos.map(todo => (
                    <div className={"todo " + (todo.complete ? "is-complete" : "")}
                        key={todo._id}
                        onClick={() => completeTodo(todo._id)} >

                        <div className="checkbox">
                            {todo.complete && <img src={redTick} alt="Checked" />}     
                        </div>

                        <div className="text">{ todo.text }</div>

                        <div className="delete-todo">X</div>
                    </div>
                ))}

            </div>
		</div>
	);
}

export default App;


