import { useState, useEffect } from "react";

const redTick = new URL("../public/tick-red.png", import.meta.url);
const addButton = new URL("../public/add-button-sr-add.png", import.meta.url);
const API_BASE = "http://localhost:3001";

function App() {

    const [todos, setTodos] = useState([]);
    const [popupActive, setPopupActive] = useState(false); //For adding new todo popup
    const [newTodo, setNewTodo] = useState(''); //New todo in add popup will be bound to this newTodo

    useEffect(() => { 
        GetTodos();

        console.log(todos)
    }, []) // [] is saying only call this when the module loads, only once. 

    const GetTodos = () => { 
        fetch(API_BASE + "/todos")
            .then(res => res.json())
            .then(data => setTodos(data))
            .catch(err => console.error("Error: ", err));
    }

    const completeTodo = async id => { 
        const data = await fetch(API_BASE + "/todo/complete/" + id)
            .then(res => res.json()); //Fetches all data

        console.log("Todos:")
        console.log(todos)

        console.log("Data:")
        console.log(data)

        setTodos(todos => todos.map(todo => {
            console.log("Todo._id: " + todo._id + " " + todo.text + "       "
                + "Data._id: " + data._id + " " + data.text)
            console.log("Todo.text: " + todo.text + " " + todo.complete + "       "
                + "Data.text: " + data.text + " " + data.complete)

            if (todo._id === data._id) { 
                todo.complete = data.complete;
                console.log("Todo._id: " + todo._id + "       " + "Data._id: " + data._id)
                console.log("Todo.complete: " + todo.complete + "       " + "Data.complete: "
                    + data.complete)
            }
            console.log("\n")
            return todo;
        }))
    }

    const deleteTodo = async id => { 
        const data = await fetch(API_BASE + "/todo/delete/" + id, {
            method: "DELETE"
        })
            .then(res => res.json());

        setTodos(todos => todos.filter(todo => todo._id !== data._id)); 
    }

	return (
		<div className="App">
            <h1>Welcome, User</h1>
            <h4>Your tasks</h4>

            <div className="todos">
                {todos.map(todo => (
                    <div className={"todo " + (todo.complete ? "is-complete" : "")}
                        key={todo._id}
                        onClick={() => completeTodo(todo._id)} >

                        <div className="checkbox">
                            {todo.complete && <img src={redTick} alt="Checked" />}     
                        </div>

                        <div className="text">{ todo.text }</div>

                        <div className="delete-todo" onClick={() => deleteTodo(todo._id) }>X</div>
                    </div>
                ))}

            </div>

            <div className="addPopup" onClick={() => setPopupActive(true)}>
                <img src={ addButton } alt="Add button" />
            </div>
		</div>
	);
}

export default App;


