import { useState, useEffect } from "react";

const redTick = new URL("../public/tick-red.png", import.meta.url);
const addButton = new URL("../public/add-button-st-add.png", import.meta.url);
const API_BASE = "http://localhost:3001";

function App() {

    const [todos, setTodos] = useState([]);
    const [popupActive, setPopupActive] = useState(false); 
    const [newTodo, setNewTodo] = useState(''); 

    useEffect(() => { 
        GetTodos();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []) 

    const GetTodos = () => { 
        fetch(API_BASE + "/todos")
            .then(res => res.json())
            .then(data => setTodos(data))
            .catch(err => console.error("Error: ", err));
    }

    const completeTodo = async id => { 
        const data = await fetch(API_BASE + "/todo/complete/" + id)
            .then(res => res.json());

        setTodos(todos => todos.map(todo => {
            if (todo._id === data._id) { 
                todo.complete = data.complete;
            }
            return todo;
        }))
    }

    const deleteTodo = async id => { 
        const data = await fetch(API_BASE + "/todo/delete/" + id, {
            method: "DELETE"
        })
            .then(res => res.json());

        setTodos(todos => todos.filter(todo => todo._id !== data._id)); 
    }

    const addTodo = async () => { 
        const data = await fetch(API_BASE + "/todo/new", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                text: newTodo
            })
        }).then(res => res.json());

        setTodos([...todos, data]);
        setPopupActive(false);
        setNewTodo("");
    }

	return (
		<div className="App">
            <h1>Welcome, <span>User</span></h1>
            <h4>Your tasks</h4>

            <div className="todos">
                {todos.map(todo => (
                    <div className={"todo " + (todo.complete ? "is-complete" : "")}
                        key={todo._id}
                        onClick={() => completeTodo(todo._id)} >

                        <div className="checkbox">
                            {todo.complete && <img src={redTick} alt="Checked" />}     
                        </div>

                        <div className="text">{ todo.text }</div>

                        <div className="delete-todo" onClick={() => deleteTodo(todo._id) }>X</div>
                    </div>
                ))}

            </div>

            <div className="addPopup" onClick={() => setPopupActive(true)}>
                <img src={ addButton } alt="Add button" />
            </div>

            {popupActive ? (
                <div className="popup">
                    <div className="closePopup"
                        onClick={() => setPopupActive(false)}>X</div>
                    <div className="content">
                            <h3>Add Task</h3>
                            <input type="text" 
                            className="add-todo-input" 
                            onChange={e => setNewTodo(e.target.value)} 
                            value={newTodo} />
                            <div className="button" onClick={ addTodo }> Create Task
                                <img src={ addButton } alt="Create task button" />
                            </div>
                     </div>   
                </div>
            ) : ''}
		</div>
	);
}

export default App;
